mapper.select:
select
  id, star, text, img_path, order_id, create_time, anonymity, reply, client_name
from HMS.remark


        and id = #{id}


        and star = #{star}


        and text = #{text}


        and img_path = #{imgPath}


        and order_id = #{orderId}


        and create_time = #{createTime}


        and anonymity = #{anonymity}


        and reply = #{reply}


        and client_name = #{clientName}
